import { useEffect } from 'react';
import Pokedex from './Pokedex';
import './Pokegame.css';

function Pokegame({ player1Name, player2Name, onScoreUpdate }) {
  // Expanded Pokemon list with more variety
  const allPokemon = [
    { id: 1, name: 'Bulbasaur', type: 'grass', base_experience: 64 },
    { id: 4, name: 'Charmander', type: 'fire', base_experience: 62 },
    { id: 7, name: 'Squirtle', type: 'water', base_experience: 63 },
    { id: 11, name: 'Metapod', type: 'bug', base_experience: 72 },
    { id: 12, name: 'Butterfree', type: 'flying', base_experience: 178 },
    { id: 25, name: 'Pikachu', type: 'electric', base_experience: 112 },
    { id: 39, name: 'Jigglypuff', type: 'normal', base_experience: 95 },
    { id: 52, name: 'Meowth', type: 'normal', base_experience: 58 },
    { id: 54, name: 'Psyduck', type: 'water', base_experience: 64 },
    { id: 94, name: 'Gengar', type: 'poison', base_experience: 225 },
    { id: 104, name: 'Cubone', type: 'ground', base_experience: 64 },
    { id: 133, name: 'Eevee', type: 'normal', base_experience: 65 },
    { id: 144, name: 'Articuno', type: 'ice', base_experience: 261 },
    { id: 145, name: 'Zapdos', type: 'electric', base_experience: 261 },
    { id: 146, name: 'Moltres', type: 'fire', base_experience: 261 },
    { id: 150, name: 'Mewtwo', type: 'psychic', base_experience: 306 }
  ];

  // Shuffle and pick 8 random Pokemon
  const shuffledAll = [...allPokemon].sort(() => Math.random() - 0.5);
  const selectedPokemon = shuffledAll.slice(0, 8);

  // Shuffle the selected 8 and split into two hands
  const shuffled = [...selectedPokemon].sort(() => Math.random() - 0.5);
  const hand1 = shuffled.slice(0, 4);
  const hand2 = shuffled.slice(4, 8);

  // Calculate total experience
  const exp1 = hand1.reduce((acc, pokemon) => acc + pokemon.base_experience, 0);
  const exp2 = hand2.reduce((acc, pokemon) => acc + pokemon.base_experience, 0);

  // Determine winner and update score
  useEffect(() => {
    if (exp1 > exp2) {
      onScoreUpdate(1);
    } else if (exp2 > exp1) {
      onScoreUpdate(2);
    }
  }, [exp1, exp2, onScoreUpdate]);

  return (
    <div className="Pokegame">
      <Pokedex
        pokemon={hand1}
        exp={exp1}
        isWinner={exp1 > exp2}
        playerName={player1Name}
      />
      <Pokedex
        pokemon={hand2}
        exp={exp2}
        isWinner={exp2 > exp1}
        playerName={player2Name}
      />
    </div>
  );
}

export default Pokegame;
