import Pokedex from './Pokedex';
import './Pokegame.css';

function Pokegame() {
  const defaultPokemon = [
    { id: 4, name: 'Charmander', type: 'fire', base_experience: 62 },
    { id: 7, name: 'Squirtle', type: 'water', base_experience: 63 },
    { id: 11, name: 'Metapod', type: 'bug', base_experience: 72 },
    { id: 12, name: 'Butterfree', type: 'flying', base_experience: 178 },
    { id: 25, name: 'Pikachu', type: 'electric', base_experience: 112 },
    { id: 39, name: 'Jigglypuff', type: 'normal', base_experience: 95 },
    { id: 94, name: 'Gengar', type: 'poison', base_experience: 225 },
    { id: 133, name: 'Eevee', type: 'normal', base_experience: 65 }
  ];

  // Shuffle and split into two hands
  const shuffled = [...defaultPokemon].sort(() => Math.random() - 0.5);
  const hand1 = shuffled.slice(0, 4);
  const hand2 = shuffled.slice(4, 8);

  // Calculate total experience
  const exp1 = hand1.reduce((acc, pokemon) => acc + pokemon.base_experience, 0);
  const exp2 = hand2.reduce((acc, pokemon) => acc + pokemon.base_experience, 0);

  return (
    <div className="Pokegame">
      <Pokedex pokemon={hand1} exp={exp1} isWinner={exp1 > exp2} />
      <Pokedex pokemon={hand2} exp={exp2} isWinner={exp2 > exp1} />
    </div>
  );
}

export default Pokegame;
